import React, {Component, useState} from 'react';


// Name, age, email, phone number, address, city, state, zipcode, sex
//Nine boxes for info
//Then 3 buttons for add, update, delete

//helpful URL: https://www.geeksforgeeks.org/how-to-connect-mongodb-with-reactjs/?ref=rp


class PatientForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: "",
            age: "", 
            email: "", 
            phoneNumber: "", 
            address: "", 
            city: "", 
            stateResidence: "", 
            zipCode: "", 
            sex: "",
            count: 0
        }
    }

    render() {
        return(
            <div className ="form-inputs">
                <button>Add</button>
                <input placeholder='Name'></input>
                <input placeholder='Age'></input>
                <input placeholder='email@example.org'></input>
                <input placeholder='phone number: 000-000-0000'></input>
                <input placeholder='Street Address'></input>
                <input placeholder='City'></input>
                <input placeholder='State'></input>
                <input placeholder='Zip Code'></input>
                <input placeholder='Sex'></input>
                <button>Delete</button>
                <button>Update</button>
                <button onClick={() => this.setState({ count: this.state.count+1 })}> {this.state.count} </button>
            </div>
        )
    }
}

export default PatientForm;
